mapper.select:
select
    job.source_datasource_id,
    job.sink_datasource_id,
    st.source_db_name,
    st.source_table_name,
    tt.sink_db_name,
    tt.sink_table_name
from (select job_id, source_table_id, sink_table_id from data_sync_table_mapping where deleted = 0  and job_id = #{jobId} ) tm
left join (select source_table_id, source_db_name, source_table_name from data_sync_source_table where deleted = 0  and job_id = #{jobId} ) st on tm.source_table_id = st.source_table_id
left join (select sink_table_id, sink_db_name, sink_table_name from data_sync_sink_table where deleted = 0  and job_id = #{jobId} ) tt on tm.sink_table_id = tt.sink_table_id
left join (select job_id, source_datasource_id, sink_datasource_id from data_sync_job where deleted = 0  and job_id = #{jobId} ) job on tm.job_id = job.job_id

     and job.source_datasource_id = #{sourceDatasourceId} 
     and st.source_db_name = #{sourceDbName} 
     and st.source_table_name = #{sourceTableName} 
     and job.sink_datasource_id = #{sinkDatasourceId} 
     and tt.sink_db_name = #{sinkDbName} 
     and tt.sink_table_name = #{sinkTableName}

select.foreach:
select
    job.source_datasource_id,
    job.sink_datasource_id,
    st.source_db_name,
    st.source_table_name,
    tt.sink_db_name,
    tt.sink_table_name
from (select job_id, source_table_id, sink_table_id from data_sync_table_mapping  where job_id = #{jobId} ) tm
left join (select source_table_id, source_db_name, source_table_name from data_sync_source_table  where job_id = #{jobId} ) st on tm.source_table_id = st.source_table_id
left join (select sink_table_id, sink_db_name, sink_table_name from data_sync_sink_table  where job_id = #{jobId} ) tt on tm.sink_table_id = tt.sink_table_id
left join (select job_id, source_datasource_id, sink_datasource_id from data_sync_job  where job_id = #{jobId} ) job on tm.job_id = job.job_id

     and job.source_datasource_id = #{sourceDatasourceId} 
     and st.source_db_name = #{sourceDbName} 
     and st.source_table_name = #{sourceTableName} 
     and job.sink_datasource_id = #{sinkDatasourceId} 
     and tt.sink_db_name = #{sinkDbName} 
     and tt.sink_table_name = #{sinkTableName}
