mapper.select:
select ds.datasource_id, ds.datasource_name, ds.`type` as datasource_type, db.db_id, db.db_name, tbl.table_id, tbl.table_name, tbl.comment, tbl.`type` as table_type from (select table_id, table_name, comment, type, db_id from meta_table where deleted = 0) tbl left join (select db_id, db_name, datasource_id from meta_db where deleted = 0) db on tbl.db_id = db.db_id left join (select datasource_id, datasource_name, type from meta_datasource where deleted = 0) ds on ds.datasource_id = db.datasource_id
and ds.datasource_id = #{datasourceId}
and db.db_name = #{dbName}
and tbl.table_name = #{tableName}

select.foreach:
select ds.datasource_id, ds.datasource_name, ds.`type` as datasource_type, db.db_id, db.db_name, tbl.table_id, tbl.table_name, tbl.comment, tbl.`type` as table_type from (select table_id, table_name, comment, type, db_id from meta_table where deleted = 0) tbl left join (select db_id, db_name, datasource_id from meta_db where deleted = 0) db on tbl.db_id = db.db_id left join (select datasource_id, datasource_name, type from meta_datasource where deleted = 0) ds on ds.datasource_id = db.datasource_id
and ds.datasource_id = #{item.datasourceId}
and db.db_name = #{item.dbName}
and tbl.table_name = #{item.tableName}
